import logo from './logo.svg';
import './App.css';
import { useState, useEffect } from 'react'

function App() {
  const [ now, setNow ] = useState(new Date())

  useEffect(() => {
    setInterval(() => {
      setNow(new Date())
    }, 1000)
  }, [])
  return (
    <div className="App">
      <header className="App-header" style={{ background: 'black' }}>
        {now.toLocaleTimeString()}
      </header>
    </div>
  );
}

export default App;
