import logo from './logo.svg';
import './App.css';
import { useState, useEffect } from 'react'
const axios = require('axios')

function App() {
  const [ now, setNow ] = useState(new Date())

  useEffect(() => {
    setInterval(() => {
      setNow(new Date())
    }, 1000)
  }, [])
  return (
    <div className="App">
      <header className="App-header" style={{ background: 'black', fontSize: 40 }}>
        <div
          style={{
            position: 'absolute',
            top: 10,
            right: 10,
            display: 'flex',
            flexDirection: 'column'
          }}
        >
          <span>{now.toLocaleDateString()}</span>
          <span>{now.toLocaleTimeString()}</span>
        </div>
      </header>
    </div>
  );
}

export default App;
