import logo from './logo.svg';
import './App.css';
import { useState, useEffect } from 'react'
const axios = require('axios')

function App() {
  const [ now, setNow ] = useState(new Date())

  useEffect(() => {
    setInterval(() => {
      setNow(new Date())
    }, 1000)
    getWeather()
  }, [])
  return (
    <div className="App">
      <header className="App-header" style={{ background: 'black', fontSize: 40 }}>
        <div
          style={{
            position: 'absolute',
            top: 10,
            left: 10,
            right: 10,
            display: 'flex',
            justifyContent: 'space-between'
          }}
        >
        <div>
          <span>Weather</span>
        </div>
          <div>
            <span>{now.toLocaleDateString()}</span>
            <br />
            <span>{now.toLocaleTimeString()}</span>
          </div>
          <div>
            <span>{now.toLocaleDateString()}</span>
            <br />
            <span>{now.toLocaleTimeString()}</span>
          </div>
        </div>
      </header>
    </div>
  );

  async function getWeather(){
    const data = await axios.get(
      'https://api.openweathermap.org/data/3.0/onecall?lat=39.4660383&lon=-104.8451618&appid=4ba4db197615321d82ad58476525afc6'
    )
    console.log(data)
  }
}

export default App;
